```csharp
public static IEnumerable<string> ReadFile(string filePath)
{
    using (StreamReader reader = new StreamReader(filePath))
    {
        string line = null;
        List<string> lines = new List<string>();
        while ((line = reader.ReadLine()) != null)
        {
            lines.Add(line);
            // Yield return 1000 lines at a time to prevent memory overflow
            if (lines.Count == 1000)
            {
                foreach (string lineToReturn in lines)
                {
                    yield return lineToReturn;
                }
                lines.Clear();
            }
        }
        // Return any remaining lines
        foreach (string lineToReturn in lines)
        {
            yield return lineToReturn;
        }
    }
}
