import java.awt.event.ActionListener;
import java.util.ArrayList;
import java.util.List;

import javax.swing.JFrame;
import javax.swing.JPanel;
import javax.swing.Timer;

public class BallBounce extends JPanel implements ActionListener {
    private static final int WIDTH = 600;
    private static final int HEIGHT = 400;
    private static final int NUM_BALLS = 2; // change this to add more balls

    private List<Ball> balls;
    private Timer timer;

    public static void main(String[] args) {
        JFrame frame = new JFrame("Ball Bounce");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setContentPane(new BallBounce());
        frame.pack();
        frame.setVisible(true);
    }

    public BallBounce() {
        balls = new ArrayList<>();
        for (int i = 0; i < NUM_BALLS; i++) {
            balls.add(new Ball(new Point(50 * i + 50, 50), Color.RED));
        }
        setPreferredSize(new Dimension(WIDTH, HEIGHT));
        timer = new Timer(16, this);
        timer.start();
    }

