from django import forms
from .models import City

class CityForm(forms.ModelForm):

    class Meta:
        model = City
        fields = ('name', 'country',)

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

        # Limit the options of the country dropdown based on the
        # selected continent, using the `ChainedSelect` widget.
